INSERT INTO #__export_content (`id`, `title`, `alias`, `title_alias`, `introtext`, `fulltext`, `state`,`sectionid`, `mask`, `catid`, `created`, `created_by`, `created_by_alias`, `modified`, `modified_by`, `checked_out`, `checked_out_time`, `publish_up`, `publish_down`, `images`, `urls`, `attribs`, `version`, `parentid`, `ordering`, `metakey`, `metadesc`, `access`, `hits`, `metadata`)VALUES('1','Rule Responder&#160;for Virtual Organizations','Pragmatic Agent Web (Web)','Pragmatic Agent Web (Web)','<h1 style=\"text-align: center\"><br /></h1><h1 style=\"text-align: center\">Rule Responder: A RuleML-Based Pragmatic Agent Web for Collaborative Teams and Virtual Organizations</h1><br /><div style=\"text-align: center\"><br /><strong>Adrian Paschke, Harold Boley, Alexander Kozlenkov, Benjamin Craig</strong><br /></div><p align=\"center\">&#x000A0;Contact: <a href=\"mailto:Rule&#717;Responder-owner@yahoogroups.com\"><u><font color=\"#0000ff\">Adrian Paschke</font></u></a></p><p align=\"center\"><br />&#x000A0;Quick Links<br /><br /><a href=\"index.php?option=com&#717;content&amp;task=view&amp;id=2&amp;Itemid=2\">Overview</a> | <a href=\"index.php?option=com&#717;content&amp;task=view&amp;id=3&amp;Itemid=5\">Use Cases</a>&#x000A0;| <a href=\"index.php?option=com&#717;content&amp;task=view&amp;id=5&amp;Itemid=7\">Download</a></p><p align=\"justify\">The Rule Responder project &#x000A0;uses&#x000A0;and extends the Semantic Web towards a <strong><em>Pragmatic Web</em></strong> (syntax+semantic+pragmatic) as an infrastructure for collaborative networks, where individuals as well as automated agents and (Web) services -- with their individual (semantic and pragmatic) contexts, decisions and efforts -- can form corporate, not-for-profit, educational, or otherwise productive <strong><em>virtual teams or virtual organizations</em></strong>. </p><p align=\"center\"><strong>Mission Statement</strong>&#x000A0;</p><p align=\"justify\">The Rule Responder project builds a methodology and an efficient rule-based middleware&#x000A0;to use and interchange knowledge&#x000A0;and rules and negotiate about their meanings. Rules and ontologies play an important role to automatically and contextually transform data, derive new conclusions and decisions from existing knowledge and act according to changed conditions&#x000A0;and&#x000A0;detected&#x000A0; events (complex event processing). Ultimately, this should put ideas of the AI community on distributed multi agent systems and novel loosely coupled&#x000A0;service component architectures&#x000A0;into large scale practice.</p><p align=\"justify\">&#x000A0;</p>','','1','1','0','1','2007-04-27 23:44:36','62','','2007-09-04 03:13:07','62','0','0000-00-00 00:00:00','2007-04-27 23:37:12','0000-00-00 00:00:00','','','show_title=1
